  const date = moment(dateValue); // Assuming dateValue is the date you want to format
  this.formattedDate = date.format('MMMM Do YYYY, h:mm:ss a'); // Customize the format as per your requirements
}

}


<div>

    <span> Customer: {{task.customerName}}</span>
    <br>

    <span> date: {{task.date}}</span>
    <br>

    <span> Task Description: {{task.taskDescription}}</span>
    <br>

    <button (click)="deleteTask()">❌</button>

</div>
